import { useEffect, useState } from "react";
import {
  connectWallet,
  getCurrentWallet,
  mintNFT
} from "./util/dapp.js";

import twitter from "./util/twitter-brands.svg"

const Minter = (props) => {
  const [status, setStatus] = useState("");
  const [walletAddress, setWallet] = useState("");
  const [mintNum, setMintNum] = useState(1);

  useEffect(async () => {
    const { status, address } = await getCurrentWallet();
    setStatus(status);
    setWallet(address);
    addWalletListener();
  }, []);

  function addWalletListener() {
    if (window.ethereum) {
      window.ethereum.on("accountsChanged", (accounts) => {
        if (accounts.length > 0) {
          setWallet(accounts[0]);
          setStatus("Wallet Connected");
        } else {
          setWallet("");
          setStatus("Connect using Metamask");
        }
      });
    } else {
      setStatus("Install Metamask");
    }
  }

  function changeNumTokens(n) {
    if((mintNum + n >= 1) && (mintNum + n <= 10)){
      setMintNum(mintNum + n)
    }
  }


  const connectWalletPressed = async () => {
    const walletResponse = await connectWallet();
    setStatus(walletResponse.status);
    setWallet(walletResponse.address);
  };

  const onMintPressed = async (numTokens) => {
    const mintRepsonse = await mintNFT(numTokens);
    // setStatus(status);
  };



  return (
    <>
    <div className="Header">

      <div className="logo">
        <h1>Cosmic Darlings</h1>
      </div>

      <div className="links">
        <button id="walletButton" onClick={connectWalletPressed}>
          {walletAddress.length > 0 ? (
            "Connected: " +
            String(walletAddress).substring(0, 6) +
            "..." +
            String(walletAddress).substring(38)
            ) : (
            <span>Connect Wallet</span>
          )}
        </button>

        <img src={twitter} alt="Kiwi standing on oval"/>

        <button id="discord">
          Discord
        </button>
      </div>

    </div>

    <div className="Minter">

      <button id="addNumToken" onClick = {() => changeNumTokens(1)}>
        +
      </button>

      <button id="minusNumToken" onClick = {() => changeNumTokens(-1)}>
        -
      </button>

      <h1 id="numberOfTokens">
        {mintNum}
      </h1>

      <button id="mintButton" onClick = {() => onMintPressed(mintNum)}>
        Mint NFT
      </button>

    </div>
    </>
  );
};

export default Minter;
