import Web3 from "web3";

const web3 = new Web3(Web3.givenProvider)

export const connectWallet = async () => {

    if (window.ethereum) {
        try {
            var wallet = await window.ethereum.request({method: 'eth_requestAccounts'});
            return {
                status: "wallet connected",
                address: wallet
            }
        }
        catch (err) {
            return {
                status: err.message,
                address: ""
            }
        }
    }

    else {
        return {
            status: "metamask not detected",
            address: ""
        }
    }
}
